+    </Grid>
+    <Grid xs={6} md={3}>
+      <MockItem text={isMd ? "2 of 2" : "3 of 3"} />
+    </Grid>
+  </Grid.Container>  
+  );
+}`;
+
+const react = {
+  '/useMediaQuery.js': useMediaQuery,
