@@ -0,0 +1,57 @@
+const App = `import { Table, useAsyncList } from '@nextui-org/react';
+    
+export default function App() {
+  const columns = [
+    { name: 'Name', uid: 'name' },
+    { name: 'Height', uid: 'height' },
+    { name: 'Mass', uid: 'mass' },
+    { name: 'Birth Year', uid: 'birth_year' }
+  ];
+  async function load({ signal, cursor }) {
+    // If no cursor is available, then we're loading the first page.
+    // Otherwise, the cursor is the next URL to load, as returned from the previous page.
+    const res = await fetch(
+      cursor || 'https://swapi.py4e.com/api/people/?search=',
+      { signal }
+    );
+    const json = await res.json();
+    return {
+      items: json.results,
+      cursor: json.next
+    };
+  }
+  const list = useAsyncList({ load });
+  return <Table
+      bordered
+      shadow={false}
+      aria-label="Example table with dynamic content & infinity pagination"
+      css={{ minWidth: '100%', height: 'calc($space$14 * 10)' }}
+      color="secondary"
+    >
+      <Table.Header columns={columns}>
+        {(column) => (
+          <Table.Column key={column.uid}>{column.name}</Table.Column>
+        )}
+      </Table.Header>
+      <Table.Body
+        items={list.items}
+        loadingState={list.loadingState}
+        onLoadMore={list.loadMore}
+      >
+        {(item) => (
+          <Table.Row key={item.name}>
+            {(key) => <Table.Cell>{item[key]}</Table.Cell>}
+          </Table.Row>
+        )}
+      </Table.Body>
+    </Table>;
+}
+`;
+
+const react = {
+  '/App.js': App
+};
+
+export default {
+  ...react
+};

@@ -0,0 +1,67 @@
+const App = `import { Table, useAsyncList, useCollator } from '@nextui-org/react';
+    
+export default function App() {
+  const collator = useCollator({ numeric: true });
+  async function load({ signal }) {
+    const res = await fetch('https://swapi.py4e.com/api/people/?search', {
+      signal
+    });
+    const json = await res.json();
+    return {
+      items: json.results
+    };
+  }
+  async function sort({ items, sortDescriptor }) {
+    return {
+      items: items.sort((a, b) => {
+        let first = a[sortDescriptor.column];
+        let second = b[sortDescriptor.column];
+        let cmp = collator.compare(first, second);
+        if (sortDescriptor.direction === 'descending') {
+          cmp *= -1;
+        }
+        return cmp;
+      })
+    };
+  }
+  const list = useAsyncList({ load, sort });
+  return <Table
+    aria-label="Example static collection table"
+    css={{ minWidth: '100%', height: 'calc($space$14 * 10)' }}
+    sortDescriptor={list.sortDescriptor}
+    onSortChange={list.sort}
+  >
+    <Table.Header>
+      <Table.Column key="name" allowsSorting>
+        Name
+      </Table.Column>
+      <Table.Column key="height" allowsSorting>
+        Height
+      </Table.Column>
+      <Table.Column key="mass" allowsSorting>
+        Mass
+      </Table.Column>
+      <Table.Column key="birth_year" allowsSorting>
+        Birth Year
+      </Table.Column>
+    </Table.Header>
+    <Table.Body items={list.items} loadingState={list.loadingState}>
+      {(item) => (
+        <Table.Row key={item.name}>
+          {(columnKey) => (
+            <Table.Cell>{item[columnKey]}</Table.Cell>
+          )}
+        </Table.Row>
+      )}
+    </Table.Body>
+  </Table>;
+}
+`;
+
+const react = {
+  '/App.js': App
+};
+
+export default {
+  ...react
+};

