+  lg: 3
+};
+
+const FeaturesGrid: React.FC<FeaturesGridProps> = ({
+  features,
+  xs,
+  sm,
+  lg,
+  css,
+  itemCss,
+  ...props
+}) => {
+  const router = useRouter();
+  const handleClick = (href: string) => {
+    router.push(href);
+  };
+
+  return (
+    <Grid.Container gap={2} css={{ px: 0, ...(css as any) }} {...props}>
